import React, { useState } from 'react';
import axios from 'axios';

const EmployerRegistrationForm = () => {
    const [formData, setFormData] = useState({
        companyName: '',
        taxCode: '',
        email: '',
        address: '',
        phoneNumber: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('https://localhost:5001/api/registration/employer', formData);
            alert(response.data);
        } catch (error) {
            console.error('There was an error registering the employer!', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input type="text" name="companyName" placeholder="Company Name" value={formData.companyName} onChange={handleChange} />
            <input type="text" name="taxCode" placeholder="Tax Code" value={formData.taxCode} onChange={handleChange} />
            <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} />
            <input type="text" name="address" placeholder="Address" value={formData.address} onChange={handleChange} />
            <input type="text" name="phoneNumber" placeholder="Phone Number" value={formData.phoneNumber} onChange={handleChange} />
            <button type="submit">Register Employer</button>
        </form>
    );
};

export default EmployerRegistrationForm;
