import React, { useState } from 'react';
import axios from 'axios';

const ApplicantRegistrationForm = () => {
    const [formData, setFormData] = useState({
        fullName: '',
        identityNumber: '',
        birthDate: '',
        gender: '',
        email: '',
        phoneNumber: '',
        address: '',
        skills: '',
        educationLevel: 'THPT'
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('https://localhost:5001/api/registration/applicant', formData);
            alert(response.data);
        } catch (error) {
            console.error('There was an error registering the applicant!', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input type="text" name="fullName" placeholder="Full Name" value={formData.fullName} onChange={handleChange} />
            <input type="text" name="identityNumber" placeholder="Identity Number" value={formData.identityNumber} onChange={handleChange} />
            <input type="date" name="birthDate" value={formData.birthDate} onChange={handleChange} />
            <select name="gender" value={formData.gender} onChange={handleChange}>
                <option value="">Select Gender</option>
                <option value="Male">Male</option>
                <option value="Female">Female</option>
                <option value="Other">Other</option>
            </select>
            <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} />
            <input type="text" name="phoneNumber" placeholder="Phone Number" value={formData.phoneNumber} onChange={handleChange} />
            <input type="text" name="address" placeholder="Address" value={formData.address} onChange={handleChange} />
            <input type="text" name="skills" placeholder="Skills" value={formData.skills} onChange={handleChange} />
            <select name="educationLevel" value={formData.educationLevel} onChange={handleChange}>
                <option value="THPT">THPT</option>
                <option value="Dai Hoc">Đại Học</option>
                <option value="Cao Hoc">Cao Học</option>
            </select>
            <button type="submit">Register Applicant</button>
        </form>
    );
};

export default ApplicantRegistrationForm;
